import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../style/RecipePage.css";

function RecipePage() {
  const [imageStatus, setImageStatus] = useState("./images/recipe/heart1.png");

  function handleClick() {
    setImageStatus("./images/recipe/heart-hover.png");
  }

  return (
    <>
      <section id="filter">{/* 这里省略部分代码 */}</section>

      <section id="cake">
        {/* 这里省略部分代码 */}

        <div className="today">
          <Link to="/recipePage/recipePage1">
            <figure className="today1">
              {/* 注意下面的代码，src 属性中使用了 imageStatus 状态 */}
              <img
                className="big-heart"
                src={imageStatus}
                alt="My Image"
                onClick={() =>
                  setImageStatus("./images/recipe/heart-hover.png")
                }
              />

              <img className="big" src="./images/recipe/big.png" alt="" />
            </figure>
          </Link>
        </div>

        {/* 这里省略部分代码 */}
      </section>
    </>
  );
}

export default RecipePage;
